package atividades;

public class Atividade2 {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Variáveis
		float nota1 = 10.0f;
		float nota2 = 8.0f;
		float nota3 = 7.0f;
		float nota4 = 7.5f;
		
		//Calculando a média
		float media = ((nota1 + nota2 + nota3 + nota4) / 4);
		
		//Saída e exibição da média
		System.out.println("A média do participante é equivalente a: " + media);
		
	}

}
